javaposse.jobdsl.plugin.ExecuteDslScript.scriptText:
def disabled_map = [:]
def r = new File("/srv/mt/jenkins/jobs/JOB-Debian-sid/workspace/from_udd")
r.eachLine { l ->
  def m = l =~ /^([^;]+);(.*)$/
  disabled_map.put(m[0][1], m[0][2])
}

def f = new File("/srv/mt/jenkins/jobs/JOB-Debian-sid/workspace/list.sid.amd64")

f.eachLine() {
  def pkgName = it
  def d_desc = disabled_map["${pkgName}"]
  def is_disabled = d_desc!=null
  d_desc = d_desc==null ? "" : d_desc

  job {
    using "JOB-Debian-sid-analysis-TMPL"
    name "sid-analysis-${pkgName}"
    description(d_desc)
    disabled(is_disabled)
    blockOn("sid-goto-cc-${pkgName}")

    wrappers {
      environmentVariables {
        env("PKG", "${pkgName}")
      }
    }

    publishers {
      if(!is_disabled) allowBrokenBuildClaiming()
    }
    configure { project ->
      project / buildWrappers / 'com.michelin.cio.hudson.plugins.copytoslave.CopyToSlaveBuildWrapper' / includes ("sid-goto-cc-${pkgName}/workspace/goto-binaries.tar.bz2")
    }
  }
}
