javaposse.jobdsl.plugin.ExecuteDslScript.scriptText:
def latest_map = [:]
def r = new File("/srv/mt/jenkins/jobs/JOB-Debian-sid-needs-build/workspace/versions.latest")
r.eachLine { l ->
  def m = l =~ /^([^;]+);(.*)$/
  latest_map.put(m[0][1], m[0][2])
}

r = new File("/srv/mt/jenkins/jobs/JOB-Debian-sid-needs-build/workspace/versions.built")
r.eachLine { l ->
  def m = l =~ /^([^;]+);(.*)$/
  if(latest_map[m[0][1]]==m[0][2]) latest_map.remove(m[0][1])
}

listView('sid-needs-build') {
  statusFilter(StatusFilter.ENABLED)

  jobs {
    latest_map.each() {
      names("sid-goto-cc-${it.key}")
    }
  }
  columns {
    status()
    name()
    lastBuildConsole()
    lastSuccess()
    lastFailure()
    lastDuration()
    buildButton()
  }
}
